/**
 * @param {string} s
 * @return {string}
 */
var longestPalindrome = function (s) {
  if (s.length < 1) {
    return '';
  }
  let longest = 0;
  let start = 0;
  let end = 0;
  for (let index = 0; index < s.length; index++) {
    const [left1, right1] = expandPalindrome(s, index, index);
    const [left2, right2] = expandPalindrome(s, index, index + 1);
    const len1 = right1 - left1;
    const len2 = right2 - left2;
    const testLongest = Math.max(len1, len2);
    if (testLongest > longest) {
      if (testLongest === len1) {
        start = left1;
        end = right1;
        longest = len1;
      } else if (testLongest === len2) {
        start = left2;
        end = right2;
        longest = len2;
      }
    }
  }

  const sub = s.substring(start, end + 1);

  console.log({
    start,
    end,
    longest: sub,
    length: sub.length,
    total: s.length
  });

  return sub;
};
var expandPalindrome = (str, left, right) => {
  let L = left;
  let R = right;
  while (L >= 0 && R <= str.length - 1 && str.charAt(L) === str.charAt(R)) {
    L--;
    R++;
  }
  return [L + 1, R - 1];
};

